// Show wFramework Errors, Warnings or Messages
jQuery(document).ready(function($) {
	wzError();
	wzWarning();
	wzMessage();
});

// Show wFramework Errors
function wzError(msg) {
	if ($('#wz-errors').length > 0) {
		//$('#wz-errors').fadeIn('slow');
		$('#wz-errors > span').each(function() {
			$.jGrowl($(this).html(), {theme: 'error', sticky: true});
		});
	}
}

// Show wFramework Warnings
function wzWarning(msg) {
	if ($('#wz-warnings').length > 0) {
		//$('#wz-warnings').fadeIn('slow');
		$('#wz-warnings > span').each(function() {
			$.jGrowl($(this).html(), {sticky: true});
		});
	}
}

// Show wFramework Messages
function wzMessage(msg) {
	if ($('#wz-messages').length > 0) {
		//$('#wz-messages').fadeIn('slow');
		$('#wz-messages > span').each(function() {
			$.jGrowl($(this).html(), {theme: 'success', sticky: true});
		});
	}
}

// Show an Error for a field
jQuery.fn.wError = function(options) {
	var $ = jQuery;
	var opts = $.extend({}, $.fn.wError.defaults, options);

	this.each(function() {
		var theID = 'wError-' + $(this).attr('id');

		if ($('#' + theID).length == 0 || $('#' + theID).html() != opts.msgTxt) {
			if ($('#' + theID).length != 0) $('#' + theID).click();
			$(this).after('<div id="' + theID + '" class="wError">' + opts.msgTxt + '</div>');
			$('#' + theID).fadeIn(opts.fadeSpeed);
			$(this).addClass('error');
		}
	});

	$('.wError').live('click',function() {
		var pID = $(this).attr('id').replace('wError-','');
		$(this).fadeOut('fast', function() {
			$(this).remove();
			$('#' + pID).removeClass('error');
		});
	});
};

jQuery.fn.wError.defaults = {
	msgTxt: 'Required',
	fadeSpeed: 'fast'
};

